UPDATE `stretchy_report` SET `report_sql`='select distinct date_format(c.activation_date, \'%Y-%m-%d\') AS activation_month,c.account_no ASaccountNo,\nc.display_name AS CustomerName,bid.serial_no as STB,bip.serial_no_2 as SC,bpr.request_type as actions,\n(select system_code from b_network_element where id = bia.provisioning_id) as Casname\nFROM b_item_attribute bia,  m_client c JOIN m_office o ON o.id = c.office_id\njoin b_item_detail bid ON bid.client_id = c.id join b_item_pairing bip ON bid.serial_no = bip.serial_no_1\njoin b_provisioning_request bpr ON bpr.client_id = c.id join b_item_master im ON bid.item_master_id = im.id\nwhere im.item_class = 1 and date_format(c.activation_date, \'%Y-%m-%d\') between \'${startDate}\' and \'${endDate}\'' WHERE report_name = 'STB Month Report ';
