INSERT IGNORE INTO `stretchy_report` (`report_name`, `report_type`, `report_subtype`, `report_category`, `report_sql`, `description`, `core_report`, `use_report`) VALUES ('Package wise Count', 'Table', '', 'BroadCaster', 'select distinct\n    pm.plan_description as PackageName,\n  (select distinct count(o.client_id) from b_orders o\njoin m_client cl on cl.id=o.client_id where o.order_status=1 and  date_format(o.active_date, \'%Y-%m-%d\') between \'${startDate}\' and \'${endDate}\'\n) as SubscribersNumber\nfrom\n    b_orders o\n        join\n    b_order_line ol ON ol.order_id = o.id\n        join\n    b_product p ON p.id = ol.product_id\n        join\n   b_plan_detail pd ON pd.product_id = p.id\n        join\n    b_plan_master pm ON pm.id = pd.plan_id\nwhere\n    o.order_status = 1 and  date_format(o.active_date, \'%Y-%m-%d\') between \'${startDate}\' and \'${endDate}\'', 'Package wise Count', '0', '1');
